    }
    else {
       	$forum_page['author_info']['warn'] .= $cur_post['uWarn'];
    }
		$forum_page['author_info']['warn'] .= '</span></li>';

		if ($cur_post['g_id']==$forum_config['o_warn_group_sig'] || $cur_post['g_id']==$forum_config['o_warn_group_ava'] || $cur_post['g_id']==$forum_config['o_warn_group_read']) $forum_page['message']['signature'] = '';
		if ($cur_post['g_id']==$forum_config['o_warn_group_ava'] || $cur_post['g_id']==$forum_config['o_warn_group_read']) $forum_page['author_ident']['avatar']  = '';
}

$section = isset($_GET['section']) ? $_GET['section'] : null;
		if ($forum_config['o_warn_enabled'] == '1' )
